  private final String eventName;
  private final int itemId;
  private final float rarity;

  public static final ImmutableMap<String, ImmutableMap<Integer, Float>> EVENT_TABLE_MAPPING = initEventMapping();

  private static ImmutableMap<String, ImmutableMap<Integer, Float>> initEventMapping()
  {
    Map<String, ImmutableMap.Builder<Integer, Float>> temp = new HashMap<>();
    for (EventRarity r : values())
    {
      ImmutableMap.Builder<Integer, Float> eventMapBuilder = temp.getOrDefault(r.eventName,
          new ImmutableMap.Builder<>());
      eventMapBuilder.put(r.itemId, r.rarity);
      temp.putIfAbsent(r.eventName, eventMapBuilder);
    }

    ImmutableMap.Builder<String, ImmutableMap<Integer, Float>> builder = new ImmutableMap.Builder<>();
    for (String eventName : temp.keySet())
    {
      builder.put(eventName, temp.get(eventName).build());
    }

    return builder.build();
  }
